def number_handle(number)
	p "Il y a #{number.grep(/\d/).length} handles qui contiennent au moins un chiffre/et ou numéro ma gl"
    return number
end

#number_handle(journalists_lists)

def aude(aude)
	p " Il y a #{aude.grep(/aude/).length} handle avec un aude dedans, t'as vu ça!"
    return (aude)
end
#aude(journalists_lists)

def uppercase_after_handle(upper)
	p "Il y a juste apres le handle  #{upper.grep(/@\p{Uppercase}/).length} majuscules, très gente dame tout ça! "
	return upper
end
#uppercase_after_handle(journalists_lists)

def uppercase_in_handle(one_upper)
	p "Y a au moins #{one_upper.grep(/\p{Uppercase}/).length} handles avec une majuscule dedans frérot, au cas où ça t'interresse?"
	return one_upper
end
#uppercase_in_handle(journalists_lists)

def underscore(underscore)
	p " Il y a #{underscore.grep(/_/).length} underscores, et rien à dire d'autres de pertinent."
	return underscore
end
#underscore(journalists_lists)

def alphabetize(alphabet)
	p "Voici un réarrangement de l'array beaucoup plus alphabetique:"
	p "#{alphabet.sort.join(",")}"
	return (alphabet)
end
#alphabetize(journalists_lists)

def short_handle(short)
	p "Les 50 handles les plus short ma gl:"
	p "#{short.sort_by(&:length)[0..49].join(", ")}"
	return short
end
