	def after_choice_player(choice)
		if choice == 1
			count_journalists(journalists_lists)
		elsif choice == 2
			number_handle(journalists_lists)
		elsif choice == 3
			aude(journalists_lists)
		elsif choice == 4
			uppercase_after_handle(journalists_lists)
		elsif choice == 5
			uppercase_in_handle(journalists_lists)
		elsif choice == 6
			underscore(journalists_lists)
		elsif choice == 7
			alphabetize(journalists_lists)
		elsif choice == 8
			short_handle(journalists_lists)
		elsif choice == 9
			think(journalists_lists)
		elsif choice == 10
			  return p " au plasir cher ami, bye bye!"
			  abort
		else 
			p "t'as beugué ou quoi? choisis une option stp frérot"
			p "                 ------------------------                 "
			ask_user
		end
		again? 
	end

	def again?
		p "Tu veux continuer? (y/n)"
		answer = gets.chomp.to_s
		if answer == "y"
			after_choice_player(ask_user)
		elsif answer == "n"
			p " au plasir cher ami, bye bye!"
		else 
			p "t'as beugué ou quoi? y ou n??"
			again?
		end
		
	end

